import React from "react";

export const ContactPicker = (props) => {
  let contactOptions = [""];

  if (props.name === false) {
    contactOptions = <option value="">No Contact Selected</option>;
    return contactOptions;
  } else {
    contactOptions = props.contacts.map((contact) => {
      <option value={contact.name} key={contact.name}>
        {" "}
        {contact.name}{" "}
      </option>;
    });
    return contactOptions;
  }

  return (
    <>
      <select onChange={props.onChange} required>{contactOptions}</select>
    </>
  );
};
