import React from "react";

export const ContactPicker = (props) => {
  let contactOptions = [""];

  if(props.name === false){
contactOptions = <option value="">No Contact Selected</option>;
    return contactOptions;
  } else {
    contactOptions = props.contacts.map((opt, i) => {
      <option value={opt.name} key={i}> {opt.name} </option>
    })
  }

    
  
  
  return (
    <>
    <select >
      {contactOptions}
    </select>
    </>
  );
};
